sys_variable_value.value:
(function execute(inputs, outputs) {

  var util = new x_exfo2_cmdb.EXFOImportUtil();
  //var folder = gs.getProperty('x_exfo2_cmdb.exfoFileFolder');
  var folder = inputs.folder_path+='/';
  var jsonObj = JSON.parse(inputs.fileObj);
  var fileList = jsonObj.fileInfos;

  var dataArray = util.buildFileArray(folder, fileList);

  outputs.data_maps = dataArray;
})(inputs, outputs);

