sys_variable_value.value:
(function execute(inputs, outputs) {
  	var parentFolder = gs.getProperty('x_exfo2_cmdb.exfoFileFolder');
  	if(parentFolder.charAt(parentFolder.length - 1) != '/'){
     	parentFolder+='/'; 
    }
	outputs.file_folder = parentFolder + inputs.import_folder;
})(inputs, outputs);

