sys_variable_value.value:
(function execute(inputs, outputs) {
	var path = inputs.file_path;
  	var splitIndex = path.lastIndexOf('/');
  	outputs.directory = path.substring(0, splitIndex);
  	outputs.file_name = path.substring(splitIndex + 1);
})(inputs, outputs);

