sys_variable_value.value:
(function execute(inputs, outputs) {
	outputs.file_folder = gs.getProperty('x_snc_exfo_cmdb.exfoFileFolder');
  	if(inputs.run_type == 'incremental'){
      outputs.last_file = gs.getProperty('x_snc_exfo_cmdb.lastIncrementalFolderProcessed');
    }
  	else if(inputs.run_type == 'full'){
      outputs.last_file = gs.getProperty('x_snc_exfo_cmdb.lastFullFolderProcessed');
    }
})(inputs, outputs);

