sys_variable_value.value:
(function execute(inputs, outputs) {
    var jsonObj = JSON.parse(inputs.file_list);
    var fileList = jsonObj.fileInfos;
    //gs.info("ALEX0: " + JSON.stringify(fileList));
    var fileListOutput = [];
    for (var i = 0; i < fileList.length; i++) {
        var fileName = fileList[i].name + '';
        if (inputs.last_file) {
            if (inputs.run_type == 'incremental')     {
                //incrementals have two timestamps.  We want the second one.
                 var timeStamp = fileName.split('_')[2];
                 var lastFileTimeStamp = inputs.last_file.split('_')[2];
            } else {
                var timeStamp = fileName.split('_')[1];
                var lastFileTimeStamp = inputs.last_file.split('_')[1];
            }     
            //gs.info('ALEX1: lastFile=' + inputs.last_file + "(" + lastFileTimeStamp + ') thisFile=' + fileName + "(" + timeStamp + ")");
            if (timeStamp > lastFileTimeStamp && fileName.indexOf(inputs.run_type) > -1) {
                outputs.import_folder = fileName;
              	break;
            }
        }
        else if(fileName.indexOf(inputs.run_type) > -1){
            outputs.import_folder = fileName;
          	break;
        }
    }
})(inputs, outputs);

