sys_rte_eb_script_operation.script:
(function(batch, output) {
	for (var i = 0; i < batch.length; i++) {
		var input = batch[i].input; 

		var util = new x_snc_exfo_cmdb.EXFOStatusDictionary();

    var snValues = util.getSNValues(input);

     output[i] = snValues;
	}
})(batch, output);
