sys_rte_eb_script_operation.script:
(function(batch, output) {
	for (var i = 0; i < batch.length; i++) {
		var input = batch[i].input; 
     var bwGR = new GlideRecord('bandwidth');
     bwGR.addQuery('name', input);
     bwGR.query();
     if(bwGR.next()){
        output[i] = bwGR.getUniqueValue();
     }
     else{
        bwGR.initialize();
        bwGR.type = 'Type';
        bwGR.name = input;
        output[i] = bwGR.insert();
     }
	}
})(batch, output);
