sys_transform_entry.source_script:
answer = (function transformEntry(source) {
    //Normally in EXFO Context data a segment (connection element) is a logical or physical link
    //But if we want to populate ports as segments for some reason, we can do that
    var ciGR = new GlideRecord('cmdb_ci');
    if (source.u_segment_has_link_key) {
        ciGR.addQuery('serial_number', source.u_segment_has_link_key);
    } else if (source.u_segment_has_a_port_key) {
        ciGR.addQuery('serial_number', source.u_segment_has_a_port_key);
    } else if (source.u_segment_has_z_port_key) {
        ciGR.addQuery('serial_number', source.u_segment_has_z_port_key);
    }

    ciGR.query();
    var returnType = '';
    if (ciGR.next()) {
        var ciSysId = ciGR.getValue('sys_id')
        var tniGR = new GlideRecord('tni_entity');
        tniGR.addQuery('ci', ciSysId);
        tniGR.query();
        while (tniGR.next()) {
            returnType = tniGR.getValue('inventory_category');
            //gs.info("AF8 TNI: " + ciGR.getValue('name') + ' ' + returnType);
        }
    }

    return returnType;
})(source);
